queries.query:
PREFIX ns2: <http://example.org/program/> 

SELECT ?smer 
WHERE { 
 ?smer ns2:has_duration ?duration 
FILTER(CONTAINS((?duration), "4")) 
}

PREFIX ns2: <http://example.org/program/>

SELECT ?duration
WHERE {
  ?smer ns2:has_duration ?duration.
  FILTER(CONTAINS(STR(?smer), "Softversko_inženjerstvo_i_informacione_tehnologije"))
}
